import { useState } from "react"; 


function SearchProduct() {
    const[productUid, setProductUid] = useState('');
    const [productDetails, setProductDetails] = useState(null);
    const [isLoading,setIsLoading] = useState(false);



    const handleProductUidChange= (event) =>{
        setProductUid(event.target.value);
    }

    const featchProductDetails =() =>{

        if(!productUid){
            alert('Please enter proper productId');
            return;
        }

        setIsLoading(true);
        fetch(`http://localhost:8080/product/id/${productUid}`)
        .then(response=>{
            return response.json();
        }).then(data =>{
            setProductDetails(data);
        }).catch(
            error => {
                console.log(error);
        }).finally(()=>{
            setIsLoading(false);
        }

        );




    }



    return <>
        <div id='productId'>
            <div id='serach'>  
                <p> Search Product : &nbsp;
                    <input type='text' id='product-uid' value={productUid} onChange={handleProductUidChange}/> &nbsp;
                    <button id='btn' onClick={featchProductDetails} disabled={isLoading}> 
                    {isLoading ? 'Loading.....' : 'Search Product'}
                    </button>
                </p>
            </div>
            <div id='body'>
                <p>Search Result</p>
                { productDetails == null ?
                    <p>Not Found</p> :
                    (
                    <>
                        <p>prodcutId : {productDetails.productUid}</p>
                        <p>Name: {productDetails.name}</p>
                        <img id='image' src={productDetails.image} alt="Product" />
                        <p>{productDetails.productType}</p>
                    </>
                    )
                }
            </div>
        </div>
    </>
}

export default SearchProduct;
